/* ******************************************************************************************
 E s t i l s    a p l i c a t s    a   l e s    3   p à g i n e s 
********************************************************************************************* */

@import url('https://fonts.googleapis.com/css?family=Fjalla+One');
@import url('https://fonts.googleapis.com/css?family=Noto+Sans:400,400i,700');
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    min-height: 100%; 
    min-width: 320px;
    font-size : 1rem;
}
body {
    font: 400 16px/1.6 'Noto Sans', sans-serif;
    color: #000;
    /* Propietats per fixar el footer   */ 
    display: flex;
    flex-direction: column;
    min-height: 100vh;
}


main {
    /* per fixar el footer */
    flex: 1;
    overflow: auto; 
    /*background-color: #FFFBD4; */
    background-color:  rgb(181, 243, 252);
}

h1,h2,h3,h4,h5 {
    font-family: 'Fjalla One', sans-serif;
    font-weight: 800;
}

p {
    font-size: 1rem;
    margin-bottom: 1rem;
}

a {
    color: #000;
    text-decoration: none;
}

a:hover {
    color: #000;
    font-weight: bold;
}

/* peu del video */
cite {
    font-size: 1rem;
    font-style: italic;
    display:block;
    text-align: center;
}

abbr {
    text-decoration: none;
}

address {
    font-style: normal;  
}

/* estils sobre imatge */
.opacitat {
    z-index: 1;
    filter: alpha(opacity=70);
    -moz-opacity: .70;
    opacity: 100;
}

.opacitat:hover {
    opacity: .70;
}

/*Clase para esconder un elemento que será visible únicamente a los lectores
 de pantalla (aplicado para mejorar la accesibilidad: enlace que permite ir
 directamente al contenido principal de la página)*/

.sr {
    position: absolute;
    height: 1px;
    width: 1px;
    overflow: hidden;
    clip: rect(1px, 1px, 1px, 1px);
}

/***************************
 Estils pel fil d'ariadna
****************************/
.fil-ariadna ul {
    display:none;   
}

@media (min-width: 768px) { 
    .fil-ariadna {
       display: list-item;
       background-color: #fff280; 
       width:100%; 
       padding-left: 3vw;
       padding-top: 0.31rem;
       padding-bottom: 0.31rem;
       font-size: 0.8rem;
       font-style: italic;
    }
    .fil-ariadna ul {
        display:contents;
        padding-left: 0.5em;
    }
    .fil-ariadna ul li {
        display: inline-block;
    }   
  }
@media (min-width: 992px) { 
    .fil-ariadna {
       font-size: 1rem;
    }
}
@media (min-width: 1200px) { 
    .fil-ariadna {
       font-size: 1.5rem;
    }
}
/****************************
Estils pel header
*****************************/

/* header: Format per dos contenidors: logo+menu-idiomes i menú principal */
header {
   /* background-color: #FF7514; */
    background-color:  rgb(0, 156, 177);
    color:black;

    /* Els contenidors logo+menu-idiomes i menu-principal; en format columnes centrades */
    display: flex;
    flex-direction: column; 
    align-items: center;
}


/* 1er Contenidor del HEADER: conté 2 items (logo i menu-idiomes) disposats en vertical */

.container-header-1 {
    width: 100%;                  /*  !!! molt important */
    display: flex;                /* contenidor flexible amb 2 items en vertical i centrat  */
    flex-direction: column-reverse;
    align-items:center;
} 
/* Container pel logo: ocupa el 15% amplada del viewport*/
.logo{
    width: 15vw;
    margin-top: 0.5rem;
    margin-bottom: 0.5rem;
}

@media (min-width: 576px) {
    .container-header-1 {
        max-width: 540px;
        width: 100%;        /*  !!! molt important */
        display: flex;      /* Contenidor flexible amb 2 items (logo i menu-idiomes ) en format horit. */
        flex-direction: row;
        justify-content: space-between;
        align-items: flex-start;
    }
    .logo{
        width: 10vw;
    }
}

@media (min-width: 768px) {
    .container-header-1 {
        max-width: 720px;
        display: flex;          /* Contenidor flexible amb 2 items (logo i menu-idiomes ) en format horit. */
        flex-direction: row;
        justify-content: space-between;
        align-items: flex-start;
    }
    .logo{
        width: 8vw;
    }
}
@media (min-width: 992px) {
    .container-header-1 {
        max-width: 960px;
        display: flex;            /* Contenidor flexible amb 2 items (logo i menu-idiomes ) en format horit. */
        flex-direction: row;
        justify-content: space-between;
        align-items: flex-start;
    }
    .logo{
        width: 7vw;
    }
}
@media (min-width: 1200px ) {
    .container-header-1 {
        max-width: 1500px;
        display: flex;   /* Contenidor flexible amb 2 items (logo i menu-idiomes ) en format horit. */
        flex-direction: row;
        justify-content: space-between;
        align-items: flex-start;
    }
    /* Container pel logo */
    .logo {
        width: 6vw;
    }
}

/* Imatges del logo */
.logo img, .logo-petit img {
    width: 100%;
    height: auto;
}

/* 2on  Contenidor del HEADER: menú principal  */
.container-header-2 {
    text-align: center;
    width: 100%; 
    background:  rgb(191, 225, 245);
    margin-right: auto;
    margin-left: auto;
    padding-top: 0.1rem;
    padding-bottom: 0.1rem;
} 
 
.logo-petit{
      width: 5vw;
      margin: 0 auto;
  }

/****************************
Estils generics pels menus 
*****************************/

nav ul {
    list-style: none;
}
nav ul li:first-child {
    padding-left: 0;
}

nav ul li:last-child {
    padding-right: 0;
}
nav ul li a.active {
    border-bottom: 2px solid #000;
    
}

/**********************************************************
Estils Menú idioma: canvi mida font en funció de dispositiu
***********************************************************/
.idioma ul li {
    display: inline-block;
    padding: 0.06rem;
    font-size: 0.9rem;
}
/* Dispositius a partir de 768px */
@media (min-width: 768px) {
    .idioma ul li {
        font-size: 1.5rem;
    }
}

/************************************
Estils menú de navegación principal
************************************/
/* Dispositius fins a 768px */
nav.menu-principal ul li {
    padding: 0px;
    margin: 0px;
    font-size: 0.9rem;
    border: 1px solid  #FFFBD4;;
}  
nav.menu-principal ul li:first-child {
    border-top: 2px solid  #FFFBD4;
}

nav.menu-principal ul li:last-child {
    border-bottom: 2px solid #FFFBD4;
}

/* Dispositius a partir de 768px */
@media (min-width: 768px) {
    nav.menu-principal ul li {
        display: inline-block;
        padding: 0.9rem;
        font-size: 1.5rem;
        border:none;
    }
    nav.menu-principal ul li:first-child {
        border-top:none;
    }
    nav.menu-principal ul li:last-child {
        border-bottom: none;
    }
}

/****************************
Estils pel footer
*****************************/
footer {

    width: 100%;
    padding: 20px 40px;
    background: rgb(0, 156, 177);
    color: black;
    text-align: center;
}

/*************************
Continguts del footer
**************************/
footer ul {
    list-style: none;
}
footer p {
    font-size: 0.8rem;
}
.text-en-dotted {
   border-bottom: 1px dotted black; 
}
/* adreça empresa   */
.adreca-empresa{
    display: block;
    font-weight: bold;
    margin-top: 10px;
    margin-bottom: 30px;
}
/* xarxes socials */
.xarxes-socials-icon {
    display: inline-block;
    background-color:  rgb(13, 109, 165);
    padding:10px;
    border-radius:30px;       
    -webkit-border-radius:30px       ;
    -moz-border-radius:30px       ;
    -ms-border-radius:30px       ;
    -o-border-radius:30px       ;
}
.container-footer-menu{
    display:block;
    margin-top: 20px;
    font-size: 1.3rem;

}
.credits  {
    display:block;
    margin-top:20px;
}
.credits  li,  .credits a {
    color: #FFFBD4;
}

.autor{
    display:block;
    font-size: 0.7rem; 
    margin-top:20px;
    margin-bottom:30px;
    text-align: center;
    font-weight: bold;
}

/****************************
Estils pel main generics
*****************************/
section {
    overflow: auto;  /* habilitar barres desplazament */
}

/* ----------LINIES NOVES   -------------- */


/* Contenidor amb: text i foro */

.portada-contenidor {
    display:flex;
    flex-direction: column; 
    margin-bottom: 1em;
     padding-top: 20px;
}
@media (min-width: 768px) { 
    .portada-contenidor {
        display:flex;
        flex-direction: row; 
        justify-content: space-between;
        align-items: flex-start;
 
    }  
}

/* contenidors items */
.portada-contenidor-text{
    width: 100%;
    margin-bottom: 1.25rem;
  
  
}
.portada-contenidor-foto{
    width: 100%;
  
 
}

@media (min-width: 768px) { 
    .portada-contenidor-text{
        width: 70%;
        margin-right: 3.12rem;
       
    }
    .portada-contenidor-foto{
        width: 30%;
        height: auto;
        padding-left: 3vw;
        border: 2px solid yellow;
        
    }
        
    
}
